import React from 'react'
import logo from '../1batlogo.png'
import './style.css'
import { useEffect } from 'react'

export const Nav = () => {
  useEffect(() => {
    window.addEventListener('scroll', isSticky);
    return () => {
        window.removeEventListener('scroll', isSticky);
    };
});


/* Method that will fix header after a specific scrollable */
       const isSticky = (e) => {
            const header = document.querySelector('.header-section');
            const scrollTop = window.scrollY;
            scrollTop >= 250 ? header.classList.add('is-sticky') : header.classList.remove('is-sticky');
        };
  return (
    <div style={{backgroundColor: 'black'}}
    className='className="header-section d-none d-xl-block"'>

      <img className="logo" src={logo} type="button" data-bs-toggle="offcanvas" data-bs-target="#offcanvasTop" aria-controls="offcanvasTop"/>
    {/* <button class="btn btn-primary" type="button" data-bs-toggle="offcanvas" data-bs-target="#offcanvasTop" aria-controls="offcanvasTop">Toggle top offcanvas</button> */}

<div class="offcanvas offcanvas-top" tabindex="-1" id="offcanvasTop" aria-labelledby="offcanvasTopLabel">
  <div class="offcanvas-header">
    <button type="button" class="btn-close text-reset" data-bs-dismiss="offcanvas" aria-label="Close"></button>
  </div>
  <div class="offcanvas-body"
  style={{fontSize: '25px'
  }}>
    <h5 id="offcanvasTopLabel">
    </h5>
    <a href="./Drums">DRUM MACHINE</a> <br />
    <a href="#">MARKETING</a><br />
    <a href="#">APPS</a> <br />
    <a href="#">PRESS</a> <br />
    MORE...
  </div>
</div>
    </div>
  )
}
export default Nav;
